import data from "../asset/json/data.json";
console.log(data.info.company)

const About = props => {
  const infos = data.info.company;
  const histories = data.info.history;
  return (
    <div className="about">
      <h3>About us</h3>
      <h4>会社概要</h4>
      <ul>
        {infos.map((info, i) => (
          <li key={i}>
            <h5>{info.title}</h5>
            <p>{info.item}</p>
          </li>
        ))}
      </ul>
      <ul>
        {histories.map((history, i) => (
          <li key={i}>
            <h5>{history.title}</h5>
            <p>{history.item}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default About;
